package week1.day2;

public class printallevenodd {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
          
		int num=10;
		System.out.println("list of  numbers 1to10");
		for(int i=1;i<=num;i++)
			if(i%2==0) {
				System.out.println(i+"even number");
			}
			else {
				System.out.println(i+"odd number");
			}
	}

} 
